  const postId = req.params.id;
  if (!postId) {
    return res
      .status(400)
      .json({ success: false, message: "Missing params id" });
  }
  try {
    const posts = await PostModel.find({ user: req.userId });
    const currentPost = posts.find((p) => p.id === postId);
    if (!currentPost) {
      return res
        .status(400)
        .json({ success: false, message: "Post not found!" });
    }
    const url = req.body.url;
    const updatePost = {
      title: req.body.title,
      status: req.body.status || "TO LEARN",
      description: req.body.description || "",
      url: url?.startsWith("https://") ? url : `https://${url}`,
    };
    const { error } = await PostValidation(updatePost);
    if (error) {
      return res.status(400).json({
        success: false,
        message: error.details[0].message,
      });
    }

    //success
    const updateCondition = { id: req.params.id, user: req.userId };
    const updatePostModel = await PostModel.findOneAndUpdate(
      updateCondition,
      updatePost,
      {
        new: true,
      }
    );

    console.log(updatePostModel);
    return res.status(200).json({
      success: true,
      message: "Update post success!",
      data: updatePost,
    });
  } catch (error) {}
};

module.exports = {
  createPost: createPost,
  readPosts: readPosts,
  updatePost: updatePost,
};

